/**
 *
 * @param address
 * @param startLength
 * @param endLength
 * @returns string
 */
const truncateWalletAddress = (address, startLength = 4, endLength = 4) => {
  return `${address.substring(0, startLength)}...${address.substring(
    address.length - endLength
  )}`;
};

export default truncateWalletAddress;
